package basics;

import java.util.Scanner;

public class ifElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner s=new Scanner(System.in);
		int a=s.nextInt();
		
		if(a==1)
			System.out.println("Deepti Ghada");
		else if(a==2)
			System.out.println("Deepti Suar");
		else if (a==3)
			System.out.println("Deepti Ullu");
		else
			System.out.println("Deepti Potty Man");

	}

}
